import React, { useEffect, useState } from 'react';

const TOTAL_QUESTIONS = 10; // Î™©Ìëú Î¨∏Ï†ú Í∞úÏàò

const QuizWord = () => {
  const [quiz, setQuiz] = useState(null);
  const [selected, setSelected] = useState('');
  const [result, setResult] = useState('');
  const [count, setCount] = useState(1);
  const [score, setScore] = useState(0);

  const fetchQuiz = async () => {
    setResult('');
    setSelected('');
    const res = await fetch('http://localhost:5000/api/quiz/word');
    const data = await res.json();
    setQuiz(data);
  };

  useEffect(() => {
    fetchQuiz();
  }, []);

  const handleChoice = (choice) => {
    setSelected(choice);
    if (choice === quiz.correct) {
      setResult('‚úÖ Ï†ïÎãµÏûÖÎãàÎã§!');
      setScore(prev => prev + 1);
    } else {
      setResult(`‚ùå Ïò§ÎãµÏûÖÎãàÎã§! Ï†ïÎãµ: ${quiz.correct}`);
    }
  };

  if (!quiz) return <p>ÌÄ¥Ï¶à Î°úÎî© Ï§ë...</p>;

  return (
    <div style={{
      padding: '2rem',
      maxWidth: '600px',
      margin: 'auto',
      background: '#ffffff',
      borderRadius: '12px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
    }}>
      
      {/* ‚úÖ ÏßÑÌñâÎ•† Î∞î */}
      <div style={{ marginBottom: '1rem' }}>
        <div style={{ fontSize: '0.9rem', color: '#888' }}>ÏßÑÌñâÎ•†: {count} / {TOTAL_QUESTIONS}</div>
        <div style={{
          background: '#e9ecef',
          borderRadius: '5px',
          overflow: 'hidden',
          height: '8px',
          marginTop: '5px'
        }}>
          <div style={{
            width: `${(count / TOTAL_QUESTIONS) * 100}%`,
            height: '100%',
            background: '#51cf66',
            transition: '0.3s'
          }}></div>
        </div>
      </div>

      <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>üß† Îúª Í≥†Î•¥Í∏∞ ÌÄ¥Ï¶à</h2>

      {/* ‚úÖ Î¨∏Ï†ú Ïπ¥Îìú */}
      <div style={{
        background: '#f1f3f5',
        padding: '1rem',
        borderRadius: '8px',
        textAlign: 'center',
        fontSize: '1.5rem',
        fontWeight: 'bold',
        marginBottom: '1rem'
      }}>
        {quiz.english}
      </div>

      {/* ‚úÖ Î≥¥Í∏∞ */}
      <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
        {quiz.choices.map((choice, idx) => (
          <button
            key={idx}
            style={{
              padding: '0.8rem',
              borderRadius: '8px',
              border: selected ? (choice === quiz.correct ? '2px solid green' : (choice === selected ? '2px solid red' : '1px solid #ccc')) : '1px solid #ccc',
              backgroundColor: selected ? (choice === quiz.correct ? '#e6ffed' : (choice === selected ? '#ffe6e6' : '#fff')) : '#fff',
              cursor: selected ? 'default' : 'pointer',
              fontSize: '1.1rem',
              transition: 'all 0.2s',
              boxShadow: selected ? (choice === quiz.correct || choice === selected ? '0 0 8px rgba(0,0,0,0.1)' : '') : '0 2px 5px rgba(0,0,0,0.05)'
            }}
            onMouseOver={(e) => { if (!selected) e.currentTarget.style.backgroundColor = '#f8f9fa'; }}
            onMouseOut={(e) => { if (!selected) e.currentTarget.style.backgroundColor = '#fff'; }}
            disabled={!!result}
            onClick={() => handleChoice(choice)}
          >
            {choice}
          </button>
        ))}
      </div>

      {/* ‚úÖ Ï†ïÎãµ ÌëúÏãú + Îã§Ïùå Î¨∏Ï†ú */}
      <div style={{ marginTop: '1.5rem', textAlign: 'center', fontSize: '1.1rem' }}>
        <p>{result}</p>
        {result && (
          <button
            onClick={() => {
              if (count < TOTAL_QUESTIONS) {
                setCount(count + 1);
                fetchQuiz();
              } else {
                alert(`ÌÄ¥Ï¶à Ï¢ÖÎ£å! Ï¥ù Ï†êÏàò: ${score} / ${TOTAL_QUESTIONS}`);
                setCount(1);
                setScore(0);
                fetchQuiz();
              }
            }}
            style={{
              padding: '0.5rem 1rem',
              borderRadius: '6px',
              border: '1px solid #888',
              background: '#f8f9fa',
              cursor: 'pointer'
            }}
          >
            {count < TOTAL_QUESTIONS ? 'Îã§Ïùå Î¨∏Ï†ú ‚ñ∂Ô∏è' : 'ÌÄ¥Ï¶à Ï¢ÖÎ£å ‚ñ∂Ô∏è'}
          </button>
        )}
      </div>

      {/* ‚úÖ Ï†êÏàò ÌëúÏãú */}
      <div style={{ marginTop: '1rem', textAlign: 'right', color: '#888' }}>
        ÌòÑÏû¨ Ï†êÏàò: {score} / {TOTAL_QUESTIONS}
      </div>

    </div>
  );
};

export default QuizWord;
